<html>
  <head>
    <style>
      /* 全局样式 */
      body {
        margin: 0;
        padding: 0;
        font-family: "KaiTi", "SimSun", serif;
        background-color: transparent;
      }

      /* 紧凑型播放器 */
      .mini-player {
        width: 240px;
        height: 80px;
        position: relative;
        border: 1px solid #d4af37;
        background-image: url("http://sharkpan.xyz/f/3Or4Fe/%E5%8F%AF%E5%95%86%E5%BA%95%E5%9B%BE1%EF%B8%8F%E2%83%A30%EF%B8%8F%E2%83%A3%E6%B3%BC%E9%87%91_2_%E5%92%95%E5%98%9F_%E6%9D%A5%E8%87%AA%E5%B0%8F%E7%BA%A2%E4%B9%A6%E7%BD%91%E9%A1%B5%E7%89%88.jpg");
        background-size: cover;
        background-position: center;
        border-radius: 5px;
        overflow: hidden;
        box-shadow: 0 2px 8px rgba(0,0,0,0.3);
        display: flex;
        align-items: center;
        margin: 10px;
      }

      /* 半透明叠加 */
      .overlay {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: linear-gradient(45deg, rgba(0,0,0,0.7), rgba(77,26,10,0.7));
        z-index: 2;
      }

      /* 角落装饰 - 更小 */
      .corner {
        position: absolute;
        width: 8px;
        height: 8px;
        border: 1px solid #d4af37;
        z-index: 3;
      }

      .corner-tl {
        top: 1px;
        left: 1px;
        border-right: none;
        border-bottom: none;
      }

      .corner-tr {
        top: 1px;
        right: 1px;
        border-left: none;
        border-bottom: none;
      }

      .corner-bl {
        bottom: 1px;
        left: 1px;
        border-right: none;
        border-top: none;
      }

      .corner-br {
        bottom: 1px;
        right: 1px;
        border-left: none;
        border-top: none;
      }

      /* 内容区 */
      .content {
        position: relative;
        z-index: 5;
        display: flex;
        width: 100%;
        padding: 0 10px;
        box-sizing: border-box;
        align-items: center;
      }

      /* 缩小版唱片 */
      .vinyl-mini {
        width: 36px;
        height: 36px;
        border-radius: 50%;
        background: radial-gradient(circle, #333 0%, #000 40%, #333 41%, #000 43%, #333 45%, #000 100%);
        box-shadow: 0 0 5px rgba(0,0,0,0.8);
        animation: spin 10s linear infinite;
        animation-play-state: paused;
        flex-shrink: 0;
        margin-right: 10px;
      }

      .vinyl-mini.playing {
        animation-play-state: running;
      }

      /* 播放控制 */
      .player-info {
        flex-grow: 1;
        display: flex;
        flex-direction: column;
      }

      .song-title {
        color: #d4af37;
        font-size: 16px;
        margin-bottom: 5px;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        text-shadow: 1px 1px 2px rgba(0,0,0,0.8);
      }

      /* 进度条 - 更小 */
      .progress-container {
        width: 100%;
        height: 3px;
        background-color: rgba(255,255,255,0.2);
        border-radius: 1.5px;
        cursor: pointer;
        margin-bottom: 5px;
      }

      .progress-bar {
        height: 100%;
        background-color: #d4af37;
        border-radius: 1.5px;
        width: 0%;
      }

      /* 控制按钮 */
      .control-area {
        display: flex;
        align-items: center;
      }

      .mini-time {
        color: #d4af37;
        font-size: 11px;
        flex-grow: 1;
      }

      .play-button {
        background-color: #d4af37;
        border: none;
        width: 24px;
        height: 24px;
        border-radius: 50%;
        color: #4d1a0a;
        font-size: 10px;
        cursor: pointer;
        display: flex;
        justify-content: center;
        align-items: center;
        margin-left: 5px;
        flex-shrink: 0;
      }

      /* 动画 */
      @keyframes spin {
        0% { transform: rotate(0deg); }
        100% { transform: rotate(360deg); }
      }

      /* 隐藏原始音频控件 */
      audio {
        display: none;
      }
    </style>
  </head>
  <body>
    <!-- disable-default-loading -->
    <div class="mini-player">
      <div class="overlay"></div>
      <div class="corner corner-tl"></div>
      <div class="corner corner-tr"></div>
      <div class="corner corner-bl"></div>
      <div class="corner corner-br"></div>

      <div class="content">
        <div class="vinyl-mini" id="vinyl"></div>

        <div class="player-info">
          <div class="song-title">金风玉露</div>
          <div class="progress-container" id="progress-container">
            <div class="progress-bar" id="progress-bar"></div>
          </div>
          <div class="control-area">
            <div class="mini-time">
              <span id="current-time">00:00</span>/<span id="total-time">00:00</span>
            </div>
            <button class="play-button" id="play-btn">
              <span id="play-icon">▶</span>
            </button>
          </div>
        </div>
      </div>

      <audio id="audio" src="http://sharkpan.xyz/f/y7YrfN/%E9%87%91%E9%A3%8E%E7%8E%89%E9%9C%B2.mp3" loop></audio>
    </div>

    <script>
      document.addEventListener('DOMContentLoaded', function() {
        const audio = document.getElementById('audio');
        const playBtn = document.getElementById('play-btn');
        const playIcon = document.getElementById('play-icon');
        const vinyl = document.getElementById('vinyl');
        const progressBar = document.getElementById('progress-bar');
        const progressContainer = document.getElementById('progress-container');
        const currentTime = document.getElementById('current-time');
        const totalTime = document.getElementById('total-time');

        // 播放/暂停功能
        playBtn.addEventListener('click', function() {
          if (audio.paused) {
            audio.play();
            playIcon.textContent = '❚❚';
            vinyl.classList.add('playing');
          } else {
            audio.pause();
            playIcon.textContent = '▶';
            vinyl.classList.remove('playing');
          }
        });

        // 更新进度条
        audio.addEventListener('timeupdate', function() {
          const progress = (audio.currentTime / audio.duration) * 100;
          progressBar.style.width = `${progress}%`;

          // 更新当前时间
          const mins = Math.floor(audio.currentTime / 60);
          const secs = Math.floor(audio.currentTime % 60);
          currentTime.textContent = `${mins}:${secs < 10 ? '0' + secs : secs}`;
        });

        // 点击进度条更新播放位置
        progressContainer.addEventListener('click', function(e) {
          const width = this.clientWidth;
          const clickX = e.offsetX;
          const duration = audio.duration;

          audio.currentTime = (clickX / width) * duration;
        });

        // 加载元数据时显示总时长
        audio.addEventListener('loadedmetadata', function() {
          const mins = Math.floor(audio.duration / 60);
          const secs = Math.floor(audio.duration % 60);
          totalTime.textContent = `${mins}:${secs < 10 ? '0' + secs : secs}`;
        });
      });
    </script>
  </body>
</html>
