<!DOCTYPE html>
<html lang="zh-CN">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>信件回忆馆</title>
    <style>
        /* --- 全局与字体设定 --- */
        @import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@400;700&display=swap');

        body {
            background-color: #f0e9dd;
            background-image: url('https://www.transparenttextures.com/patterns/old-wall.png');
            font-family: 'Noto Serif SC', serif;
            margin: 0;
            padding: 20px;
            display: flex;
            justify-content: center;
            align-items: flex-start;
            min-height: 100vh;
            color: #4a4035;
        }

        /* --- 主容器 --- */
        .letter-viewer-container {
            display: flex;
            flex-direction: row;
            width: 100%;
            max-width: 1200px;
            background-color: #fdf5e6;
            border: 2px solid #dcd1bb;
            box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2), 0 0 0 10px #eaddc7;
            border-radius: 12px;
            overflow: hidden;
            animation: fadeIn 1.5s ease-in-out;
        }

        @keyframes fadeIn {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
        }

        /* --- 缩略图侧边栏 --- */
        .thumbnail-sidebar {
            width: 200px;
            flex-shrink: 0;
            background-color: #eaddc7;
            border-right: 2px solid #dcd1bb;
            padding: 20px 10px;
            overflow-y: auto;
            box-shadow: inset 5px 0 15px -5px rgba(0,0,0,0.1);
        }

        .thumbnail-sidebar h3 {
            text-align: center;
            margin-top: 0;
            margin-bottom: 20px;
            font-weight: 700;
            color: #6b4f3a;
            border-bottom: 1px solid #c5b8a5;
            padding-bottom: 10px;
        }

        .thumbnail-img {
            width: 100%;
            height: auto;
            margin-bottom: 15px;
            border-radius: 6px;
            border: 4px solid transparent;
            cursor: pointer;
            opacity: 0.7;
            transition: all 0.3s ease;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }

        .thumbnail-img:hover {
            opacity: 1;
            transform: scale(1.05);
        }

        .thumbnail-img.active {
            opacity: 1;
            border-color: #a5465e; /* 火漆印一般的红色边框 */
            box-shadow: 0 0 15px rgba(165, 70, 94, 0.5);
            transform: scale(1.05);
        }

        /* 自定义滚动条 */
        .thumbnail-sidebar::-webkit-scrollbar {
            width: 8px;
        }
        .thumbnail-sidebar::-webkit-scrollbar-track {
            background: #eaddc7;
        }
        .thumbnail-sidebar::-webkit-scrollbar-thumb {
            background-color: #c5a880;
            border-radius: 10px;
            border: 2px solid #eaddc7;
        }


        /* --- 主显示区域 --- */
        .main-display {
            flex-grow: 1;
            padding: 30px;
            display: flex;
            justify-content: center;
            align-items: center;
            overflow: hidden;
        }

        #current-letter-img {
            max-width: 100%;
            max-height: calc(100vh - 100px);
            object-fit: contain;
            border-radius: 8px;
            box-shadow: 0 8px 25px rgba(0,0,0,0.15);
            border: 1px solid #ccc;
            animation: imageSwap 0.5s ease-in-out;
        }

        @keyframes imageSwap {
            from { opacity: 0.3; transform: scale(0.95); }
            to { opacity: 1; transform: scale(1); }
        }

        /* --- 响应式设计 --- */
        @media (max-width: 768px) {
            body {
                padding: 10px;
            }
            .letter-viewer-container {
                flex-direction: column;
                box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2), 0 0 0 5px #eaddc7;
            }
            .thumbnail-sidebar {
                width: 100%;
                height: 150px;
                display: flex;
                flex-direction: row;
                overflow-x: auto;
                overflow-y: hidden;
                border-right: none;
                border-bottom: 2px solid #dcd1bb;
                padding: 10px;
                box-sizing: border-box;
            }
            .thumbnail-sidebar h3 {
                display: none; /* 在小屏幕上隐藏标题以节省空间 */
            }
            .thumbnail-img {
                width: auto;
                height: 100%;
                margin-bottom: 0;
                margin-right: 10px;
            }
            .main-display {
                padding: 20px 10px;
            }
            #current-letter-img {
                 max-height: calc(100vh - 200px);
            }
        }
    </style>
</head>
<body>

    <div class="letter-viewer-container">
        <div class="thumbnail-sidebar" id="thumbnail-gallery">
            <h3>信件列表</h3>
            <!-- 缩略图将由JavaScript动态生成 -->
        </div>
        <div class="main-display">
            <img id="current-letter-img" src="" alt="当前信件">
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {

            // 所有信件图片的链接
            const letters = [
                { id: '6.31', from: 'B', page: 1, url: 'https://files.catbox.moe/ae6hl3.jpg' },
                { id: '6.31', from: 'A', page: 2, url: 'https://files.catbox.moe/jq6h4i.jpg' },
                { id: '6.8', from: 'A', page: 1, url: 'https://files.catbox.moe/c7evzc.jpg' },
                { id: '6.8', from: 'A', page: 2, url: 'https://files.catbox.moe/69r12r.jpg' },
                { id: '6.11', from: 'B', page: 1, url: 'https://files.catbox.moe/5vlald.jpg' },
                { id: '6.11', from: 'B', page: 2, url: 'https://files.catbox.moe/20kdsg.jpg' },
                { id: '7.3', from: 'A', page: 1, url: 'https://files.catbox.moe/h9fr6p.jpg' },
                { id: '7.3', from: 'A', page: 2, url: 'https://files.catbox.moe/05lxzx.jpg' },
                { id: '7.7', from: 'B', page: 1, url: 'https://files.catbox.moe/u3o8ix.jpg' },
                { id: '7.7', from: 'B', page: 2, url: 'https://files.catbox.moe/qguuaw.jpg' },
                { id: '8.3', from: 'A', page: 1, url: 'https://files.catbox.moe/wr9on0.jpg' },
                { id: '8.3', from: 'A', page: 2, url: 'https://files.catbox.moe/vg1idg.jpg' },
                { id: '8.6', from: 'B', page: 1, url: 'https://files.catbox.moe/lkw0fk.jpg' },
                { id: '8.6', from: 'B', page: 2, url: 'https://files.catbox.moe/mro1m5.jpg' }
            ];

            const gallery = document.getElementById('thumbnail-gallery');
            const mainImage = document.getElementById('current-letter-img');

            // 动态生成缩略图并添加到侧边栏
            letters.forEach((letter, index) => {
                const thumb = document.createElement('img');
                thumb.src = letter.url;
                thumb.alt = `信件 ${letter.id} - 第${letter.page}页`;
                thumb.className = 'thumbnail-img';
                thumb.dataset.index = index; // 使用data属性存储索引
                gallery.appendChild(thumb);

                // 为每个缩略图添加点击事件
                thumb.addEventListener('click', function() {
                    displayLetter(index);
                });
            });

            // 函数：用于显示指定索引的信件
            function displayLetter(index) {
                // 更新主图片
                mainImage.src = letters[index].url;
                mainImage.alt = `信件 ${letters[index].id} - 第${letters[index].page}页`;

                // 更新高亮显示的缩略图
                const allThumbs = document.querySelectorAll('.thumbnail-img');
                allThumbs.forEach(thumb => {
                    thumb.classList.remove('active');
                });
                allThumbs[index].classList.add('active');
            }

            // 页面加载时，默认显示第一封信
            if (letters.length > 0) {
                displayLetter(0);
            }

        });
    </script>
</body>
</html>
