            try {
                const messages = await getChatMessages("0", { include_swipe: true });

                if (!messages || messages.length === 0 || !messages[0].swipes || messages[0].swipes.length <= 1) {
                    container.innerHTML = '<div style="text-align:center; padding:20px; font-size:12px; opacity:0.7;">æš‚æ— é¢å¤–å¼€åœºç™½ï¼Œè¯·ç›´æ¥å›å¤å¼€å§‹ã€‚</div>';
                    return;
                }

                const swipes = messages[0].swipes;

                // æ¸…ç©ºå®¹å™¨
                container.innerHTML = '';

                // éå†swipes (è·³è¿‡ç´¢å¼•0çš„ä¸»é¡µ)
                for (let i = 1; i < swipes.length; i++) {
                    const content = swipes[i];
                    let title = `Story Option ${i}`;
                    let description = "ç‚¹å‡»å¼€å¯è¿™æ®µæ•…äº‹...";

                    // è§£ææ³¨é‡Š <!-- title: x --> å’Œ <!-- desc: x -->
                    const titleMatch = content.match(/<!--\s*title:\s*(.*?)\s*-->/s);
                    if (titleMatch) title = titleMatch[1].trim();

                    const descMatch = content.match(/<!--\s*desc:\s*(.*?)\s*-->/s);
                    if (descMatch) description = descMatch[1].trim();

                    // åˆ›å»ºHTMLå…ƒç´ 
                    const item = document.createElement('div');
                    item.className = 'opening-item';
                    item.innerHTML = `
                        <span class="opening-title">${title}</span>
                        <span class="opening-desc">${description}</span>
                    `;

                    // ç»‘å®šç‚¹å‡»äº‹ä»¶ (å¸¦æ¶Ÿæ¼ªå’Œåé¦ˆ)
                    item.onclick = (e) => {
                        e.stopPropagation(); // é˜²æ­¢è§¦å‘èƒŒæ™¯çš„æ³¢çº¹
                        switchToOpening(i, item);
                    };

                    container.appendChild(item);
