        #nav-general:checked ~ .iphone-container .screen#general-chat-screen,
        #nav-hunter:checked ~ .iphone-container .screen#hunter-chat-screen,
        #nav-blair:checked ~ .iphone-container .screen#blair-chat-screen {
            display: flex;
        }

        /* --- 通用顶部栏 --- */
        .ios-top-bar {
            flex-shrink: 0;
            padding: 12px 20px 8px;
            display: flex;
            justify-content: space-between;
            align-items: center;
            font-size: 13px;
            font-weight: 600;
            color: var(--ios-text-dark);
            border-bottom: 1px solid var(--ios-border-gray);
        }

        /* --- 联系人列表页 --- */
        #contacts-screen .header-title {
            font-family: 'Impact', 'Arial Black', sans-serif;
            font-size: 28px;
            text-align: center;
            padding: 20px 20px 10px;
            letter-spacing: 1px;
            color: #222;
        }

        .author-note {
            font-size: 13px;
            color: var(--ios-text-light);
            background: var(--ios-light-gray);
            padding: 10px 20px;
            margin: 0 15px 15px;
            border-radius: 10px;
            line-height: 1.5;
            text-align: center;
        }

        .contact-list {
            padding: 0 10px;
            flex-grow: 1;
        }

        .contact-item {
            display: flex;
            align-items: center;
            padding: 12px 10px;
            cursor: pointer;
            border-bottom: 1px solid var(--ios-light-gray);
        }
        .contact-item:last-child { border-bottom: none; }
        .contact-item:hover { background: #f8f8f8; }

        .contact-avatar {
            width: 50px;
            height: 50px;
            border-radius: 50%;
            margin-right: 15px;
            object-fit: cover; /* 确保图片不变形地充满圆形区域 */
            background-color: #e5e5e5; /* 图片加载前的占位背景色 */
            flex-shrink: 0;
        }

        .contact-info { flex-grow: 1; overflow: hidden; }
        .contact-name { font-size: 17px; font-weight: 600; }
        .contact-preview {
            font-size: 14px;
            color: var(--ios-text-light);
            overflow: hidden;
            text-overflow: ellipsis;
            white-space: nowrap;
        }
        .contact-arrow {
            font-size: 20px;
            color: #c7c7cc;
            flex-shrink: 0;
        }

        /* --- 聊天信息页 --- */
        .chat-header {
            display: flex;
            align-items: center;
            padding: 10px;
            border-bottom: 1px solid var(--ios-border-gray);
            background: #f8f8f8;
        }
        .back-button {
            cursor: pointer;
            color: var(--ios-blue);
            font-size: 17px;
            display: flex;
            align-items: center;
        }
        .back-button-icon {
            width: 13px;
            height: 21px;
            margin-right: 5px;
        }
        .chat-contact-title {
            flex-grow: 1;
            text-align: center;
        }
        .chat-contact-title .name { font-weight: 600; font-size: 17px; }
        .chat-contact-title .status { font-size: 12px; color: var(--ios-text-light); }
        .placeholder { width: 50px; } /* 占位用 */

        .message-container {
            flex-grow: 1;
            padding: 20px 10px;
            display: flex;
            flex-direction: column;
            background: #fff;
        }

        .message-bubble {
            all: unset;
            display: block;
            box-sizing: border-box;
            padding: 10px 15px;
            border-radius: 18px;
            background-color: var(--ios-light-gray);
            max-width: 80%;
            margin-bottom: 10px;
            align-self: flex-start;
            font-size: 16px;
            line-height: 1.4;
            cursor: pointer;
            text-align: left;
            position: relative;
        }
        .message-bubble.sent {
            background-color: var(--ios-blue);
            color: #fff;
            align-self: flex-end;
        }
        .message-bubble.option:hover {
             filter: brightness(0.95);
        }
        .message-bubble.danger-option {
            background-color: var(--danger-red);
            color: #fff;
        }
        .message-bubble::before { /* 模拟消息气泡的小尾巴 */
            content: '';
            position: absolute;
            bottom: 0;
            left: -5px;
            width: 10px;
            height: 10px;
            background: var(--ios-light-gray);
            clip-path: path('M0 10 C 4 10, 8 8, 10 5 L 10 10 L 0 10 Z');
        }
        .message-bubble.danger-option::before {
            background: var(--danger-red);
        }
    </style>
</head>
