    <script>
        // --- 数据定义 ---
        const styleOptions = {
            "现代": ["真假少爷", "校园暗恋", "破镜重圆", "都市情缘", "娱乐圈"],
            "古代": ["宫廷权谋", "江湖恩怨", "修仙问道", "种田经商", "朝堂之上"],
            "西幻": ["魔法学院", "骑士与龙", "神明与圣子", "亡灵序曲", "种族战争"],
            "玄幻": ["废柴逆袭", "天才陨落", "宗门大比", "上古秘境", "异火情缘"],
            "未来": ["星际探索", "机甲战争", "人工智能", "赛博朋克", "废土求生"],
            "ABO": ["强制匹配", "宿敌变O", "Alpha伪装", "信息素依赖", "腺体破损"],
            "虫族": ["雄虫至上", "雌虫逆袭", "精神链接", "虫纹觉醒", "远征异星"],
            "哨向": ["精神图景", "结合热", "首席哨兵", "黑暗哨兵", "向导学院"],
            "修仙": ["师徒情深", "炉鼎逆袭", "剑道无情", "魔尊追妻", "飞升之路"],
            "末世": ["末世废土", "异能觉醒", "基地风云", "丧尸围城", "人性考验"],
            "无限流": ["无限流", "生存游戏", "闯关解谜", "团队合作", "Boss攻略"]
        };

        // --- 初始化 ---
        document.addEventListener('DOMContentLoaded', init);
        // SillyTavern中, DOMContentLoaded可能已过, 直接调用
        if (document.readyState === "complete" || document.readyState === "interactive") {
            init();
        }

        function init() {
            // 绑定标签页切换事件
            const tabs = document.querySelectorAll('.tab-button');
            tabs.forEach(tab => {
                tab.addEventListener('click', () => switchTab(tab.dataset.tab));
            });

            // 绑定小说分类的变更事件
            const categorySelect = document.getElementById('category');
            if (categorySelect) {
                categorySelect.addEventListener('change', updateStyleOptions);
                // 初始化小说风格选项
                updateStyleOptions();
            }
        }

        // --- 功能函数 ---

        // 切换标签页
        function switchTab(tabId) {
            document.querySelectorAll('.tab-button').forEach(btn => btn.classList.remove('active'));
            document.querySelector(`.tab-button[data-tab="${tabId}"]`).classList.add('active');

            document.querySelectorAll('.creator-panel').forEach(panel => panel.classList.remove('active'));
            document.getElementById(`panel-${tabId}`).classList.add('active');
        }

        // 更新小说风格下拉框
        function updateStyleOptions() {
            const category = document.getElementById('category').value;
            const styleSelect = document.getElementById('style');
            const options = styleOptions[category] || [];

            styleSelect.innerHTML = ''; // 清空现有选项
            options.forEach(optionText => {
                const option = document.createElement('option');
                option.value = optionText;
                option.textContent = optionText;
                styleSelect.appendChild(option);
            });
        }

        // 切换自定义输入框的显示
        function toggleCustomInput(selectElement) {
            const customInput = selectElement.nextElementSibling;
            if (selectElement.value === 'custom') {
                customInput.style.display = 'block';
                customInput.focus();
            } else {
                customInput.style.display = 'none';
            }
        }

        // 生成世界
        function generateWorld() {
            const activePanelId = document.querySelector('.creator-panel.active').id;
            const form = document.getElementById(activePanelId.replace('panel-', 'form-'));
            const formData = new FormData(form);
            const worldSettings = {};
            let descriptionParts = [];

            for (let [key, value] of formData.entries()) {
                if (key.endsWith('_custom')) continue; // 跳过自定义输入框本身

                if (value === 'custom') {
                    const customValue = formData.get(`${key}_custom`);
                    worldSettings[key] = customValue || '未指定';
                    descriptionParts.push(`${getLabel(key)}: ${customValue || '未指定'}`);
                } else {
                    worldSettings[key] = value;
                    descriptionParts.push(`${getLabel(key)}: ${value}`);
                }
            }

            const description = descriptionParts.join(', ');
            const command = `/echo severity=success 正在为您编织新的世界... | /send 生成世界，设定如下：[${description}] | /trigger`;

            if (typeof triggerSlash === 'function') {
                triggerSlash(command);
            } else {
                console.warn("SillyTavern's triggerSlash function is not available.");
                alert("命令已生成，请查看控制台输出：\n" + command);
            }
        }

        // 辅助函数：获取标签文本
        function getLabel(key) {
            const labels = {
                'category': '分类', 'style': '风格', 'cp_relationship': '攻受关系',
                'user_role': '你的身份', 'difficulty': '难度'
            };
            return labels[key] || key;
        }

    </script>
