                const descMatch = content.match(/<!--\s*desc:\s*(.*?)\s*-->/s);
                if (descMatch) description = descMatch[1].trim();

                const item = document.createElement('div');
                item.className = 'opening-card';
                item.innerHTML = `
                    <div class="op-title"><span>${title}</span><span class="op-index">#0${i}</span></div>
                    <div class="op-desc">${description}</div>
                `;
                item.onclick = async () => {
                    item.style.borderLeftColor = '#fff';
                    if (typeof setChatMessages === 'function') {
                        await setChatMessages([{message_id: 0, swipe_id: i}]);
                    }
                };
                container.appendChild(item);
            }

        } catch (error) {
            console.error(error);
            container.innerHTML = '<div style="color:red;padding:20px;">系统错误，读取失败。</div>';
        }
    }

    // 初始化
    setTimeout(loadOpenings, 500);

</script>
</body>
</html>
```
