  const CARD_INTERVAL = 300; // 卡片出现间隔

  let pressTimer;
  let isUnlocked = false;

  // 1. 锁屏交互逻辑
  function handlePressStart(e) {
    if(isUnlocked) return;
    e.preventDefault();

    fpTrigger.classList.add('scanning');
    statusMsg.textContent = "VERIFYING BIOMETRICS...";
    statusMsg.style.color = "#fff";

