    fpTrigger.classList.remove('scanning');
    statusMsg.textContent = "ACCESS DENIED. RETRY.";
    statusMsg.style.color = "#ff4444";

    lockScreen.classList.add('shake');
    setTimeout(() => lockScreen.classList.remove('shake'), 400);
  }

  // 绑定事件
  fpTrigger.addEventListener('mousedown', handlePressStart);
  fpTrigger.addEventListener('touchstart', handlePressStart);
  window.addEventListener('mouseup', handlePressEnd);
  window.addEventListener('touchend', handlePressEnd);

