  }
  .employee-card summary::-webkit-details-marker {
    display: none;
  }
  .card-avatar {
    grid-row: 1 / span 2;
    width: 90px;
    height: 90px;
    border-radius: 50%;
    border: 3px solid #007bff;
    object-fit: cover;
  }
  .card-company-logo {
    grid-column: 2;
    grid-row: 1;
    font-size: 18px;
    font-weight: bold;
    color: #0056b3;
    text-align: left;
