    <script>
        async function jumpToScene(swipeIndex) {
            try {
                // 检查是否在SillyTavern环境中
                if (typeof getChatMessages !== 'function' || typeof setChatMessage !== 'function') {
                    console.warn("API unset. Simulation Mode.");
                    alert(`【模拟环境】尝试跳转到开场白索引：${swipeIndex}`);
                    return;
                }

                // 获取当前聊天的消息列表
                const messages = await getChatMessages("0"); // "0" 通常代表当前聊天
                if (!messages || messages.length === 0) throw new Error("无法获取消息历史");

                // 获取第一条消息（通常包含开场白的所有swipe）
                const firstMessage = messages[0];
                const totalSwipes = firstMessage.swipes.length;

                // 索引越界检查
                if (swipeIndex < 0 || swipeIndex >= totalSwipes) {
                    alert(`该开场白暂未启用 (索引: ${swipeIndex})。请确保您已添加了对应数量的开场白。`);
                    return;
                }

                // 执行跳转和刷新
                await setChatMessage(
                    { message: firstMessage.swipes[swipeIndex] },
                    0,
                    { swipe_id: swipeIndex, refresh: 'display_and_render_current' }
                );

            } catch (error) {
                console.error("跳转失败:", error);
                alert("跳转指令执行失败，请检查控制台或手动切换卡片。");
            }
        }

        // 简单的入场动画控制
