  // Slider Logic
  tightnessSlider.addEventListener('input', (e) => {
    const val = e.target.value;
    tightnessVal.innerText = `Level ${val}`;

    // Color change
    if(val > 7) {
      tightnessVal.style.color = 'var(--danger)';
      targetBpm = 95 + (val * 2); // Heart rate goes up with pressure
    } else {
      tightnessVal.style.color = 'var(--primary)';
      targetBpm = 72 + (val * 1);
    }
  });

  tightnessSlider.addEventListener('change', (e) => {
    const val = e.target.value;
    addLog(`调整压力等级至 Lv.${val}`);

    if(val > 8) {
      showModal("⚠️ 压力过载警告", "检测到束缚环内壁压力过高。如果持续该状态超过10分钟，可能会在对象脚踝处留下难以消除的淤青与红痕。<br><br>矢原警官，您确定要这么做吗？");
    } else if (val > 4) {
      showModal("ℹ️ 压力调整", "气囊充气中...<br>对象可能会感到脚踝处有明显的紧缚感和轻微麻木。这种时刻提醒被监管身份的感觉，或许正是您想要的。");
    }
  });

  // Actions
  function triggerAction(type) {
    if (type === 'check') {
      addLog("正在扫描对象体征...");
      setTimeout(() => {
        showModal("🔍 扫描完成", "体温: 36.4°C (偏低)<br>皮肤湿度: 干燥<br>荷尔蒙水平: 正常<br><br>备注：对象虽然看起来很冷静，但颈部脉搏有轻微加速。");
        targetBpm = 85;
      }, 800);
    } else if (type === 'shock') {
      deviceContainer.classList.add('shake');
      setTimeout(() => deviceContainer.classList.remove('shake'), 500);

      addLog("!!! 发送低压电流警示 !!!");
      targetBpm = 120; // Heart rate spikes

      setTimeout(() => {
        showModal("⚡ 电流警示已执行", "电流强度: 2级 (刺痛)<br>持续时间: 0.5s<br><br>这也是‘矫正’的一部分。让他记住谁手里捏着开关，不是吗？<br>请观察对象的大腿肌肉是否有痉挛反应。");
      }, 600);
    }
  }

  // Logger
  function addLog(msg) {
    const now = new Date();
    const timeStr = now.toTimeString().split(' ')[0];
    const div = document.createElement('div');
    div.className = 'log-entry';
    div.innerHTML = `<span class="log-time">${timeStr}</span>${msg}`;
    logTerminal.insertBefore(div, logTerminal.firstChild);
  }

  // Modal Control
  function showModal(title, content) {
    modalTitle.innerHTML = title;
    modalContent.innerHTML = content;
    modal.style.display = 'flex';
  }

  function closeModal() {
    modal.style.display = 'none';
  }

</script>
