            { user: 'user-fox', id: 'Fox_C (程)', text: '嘘...看来这出戏的观众，不止我们两个呢。' }
        ];

        let index = 0;
        const btn = document.getElementById('decryptBtn');
        const chatContainer = document.getElementById('chatContainer');

        if(btn) {
            btn.addEventListener('click', function() {
                if (index < messages.length) {
                    const msgData = messages[index];
                    const msgDiv = document.createElement('div');
                    msgDiv.className = `message ${msgData.user}`;
                    msgDiv.innerHTML = `<span class="user-id">${msgData.id}</span><div class="content-text">${msgData.text}</div>`;
                    chatContainer.appendChild(msgDiv);

                    // Force reflow for animation
                    void msgDiv.offsetWidth;

                    msgDiv.classList.add('show');
                    chatContainer.scrollTop = chatContainer.scrollHeight;

                    index++;
                    if (index === messages.length) {
                        btn.textContent = "SIGNAL LOST // 连接中断";
                        btn.style.border = "1px solid #ff0055";
                        btn.style.color = "#ff0055";
                        btn.style.boxShadow = "none";
                    }
                }
            });
        }
    })();
</script>

